/*
color variables
*/
*,
*:before,
*:after {
  margin: 0;
  padding: 0;
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
}

body {
  font-family: "Open Sans";
  color: #000000;
  font-size: 1.6rem;
  line-height: 1.7;
  font-weight: 400;
  letter-spacing: 1.2;
}

ul,
li {
  list-style: none;
}

a {
  text-decoration: none;
}

h1 {
  font-size: 3.6rem;
}

h2 {
  font-size: 2.4rem;
}

.btn {
  border-radius: 5rem;
  padding: 1rem 2rem;
  color: #000000;
}
.btn-primary {
  background: #000000;
  color: #ffd700;
}

.notifyBar {
  position: relative;
  background: #000000;
  height: 4rem;
  width: 100%;
  text-align: center;
  display: flex;
  justify-content: center;
  align-items: center;
  color: #d8d8d8;
  font-weight: 700;
}
.notifyBar-content {
  width: 100%;
}
.notifyBar-text {
  text-transform: uppercase;
  font-size: 1.4rem;
}
.notifyBar-close {
  position: absolute;
  top: 0px;
  right: 0;
  padding: 0.5rem 2rem;
  font-size: 1.6rem;
}

.mainQuote {
  background: rgba(179, 179, 179, 0.2);
  text-align: center;
  padding: 5rem;
  width: 100%;
}
.mainQuote q {
  width: 50%;
}

.sliderWrapper,
#slider {
  overflow: hidden;
  height: 70vh;
  width: 70%;
}

.slide-content {
  overflow: hidden;
  height: 70vh;
}

.sliderWrapper {
  position: relative;
}

#slider {
  margin: 0 auto;
}

.slide {
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  transition: transform 0.3s ease-in-out;
}
.slide-1 {
  background-image: url(/eeeb90e29ad0bcfa5446099b3d787545.jpg);
}
.slide-2 {
  background-image: url(/730793788840739841046061af97a938.jpg);
}
.slide-3 {
  background-image: url(/a4090f54e872d47cd8f1ad002a9f719f.jpg);
}
.slide-4 {
  background-image: url(/17fb3fe9f5a3b132e3ed0220e187ab85.jpg);
}
.slide-5 {
  background-image: url(/25ef9d6304ae0c0acaca236a42ae0118.jpg);
}
.slide-6 {
  background-image: url(/05e4b5e00780e0cc00ab281325476f34.jpg);
}
.slide-7 {
  background-image: url(/164030a6328e0991691e176a39bb420e.jpg);
}
.slide-8 {
  background-image: url(/8d86d27d1830b19ea3d4e881a60debde.jpg);
}
.slide-content {
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  align-items: center;
}
.slide-content span {
  color: white;
  font-size: 2.4rem;
}

.arrow {
  cursor: pointer;
  position: absolute;
  top: 50%;
  margin-top: -1.5rem;
  width: 0;
  height: 0;
  border-style: solid;
}

#arrowLeft {
  border-width: 1.5rem 2.5rem 1.5rem 0;
  left: 0;
  margin-left: 10rem;
  border-color: transparent #fff transparent transparent;
}

#arrowRight {
  border-width: 1.5rem 0 1.5rem 2.5rem;
  right: 0;
  margin-right: 10rem;
  border-color: transparent transparent transparent #fff;
}

.navDot {
  text-align: center;
  margin-left: 25%;
}
.navDot-dots {
  height: 1.5rem;
  width: 1.5rem;
  margin: 0.5rem;
  background-color: #ffffff;
  border-radius: 50%;
  display: inline-block;
}
.navDot-active {
  background-color: #ffd700;
}

.box {
  border-radius: 0.5rem;
  margin-right: 5rem;
  box-shadow: 0 0 2rem rgba(0, 0, 0, 0.1);
  padding: 5rem;
  width: 375px;
  background: #ffffff;
}
.box-head {
  text-align: center;
  margin: 2rem 0;
}
.box-head img {
  width: 10rem;
}
.sections {
  padding: 20rem 20rem 25rem 20rem;
}

header {
  font-weight: 700;
  text-transform: uppercase;
  padding: 1rem 0px;
  font-size: 1.4rem;
}
header .nav {
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 10rem;
  position: relative;
}
header .navMenu {
  display: flex;
}
