  width: 12rem;
}

.hero {
  position: relative;
}

.bgVideo {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 80vh;
  z-index: -1;
}
.bgVideo-content {
  height: 100%;
  width: 100%;
  object-fit: cover;
}

.heroContent {
  height: 80vh;
}
.heroContent-heading {
  position: relative;
  top: 40%;
  left: 50%;
  width: 50%;
  transform: translate(-50%, -40%);
  text-align: center;
  font-size: 7rem;
  color: #ffd700;
  font-weight: 800;
}

.sectionMembers-head {
  text-align: center;
  margin: 5rem auto;
  width: 25%;
  text-transform: uppercase;
  border-bottom: 0.2rem solid gold;
}

.membersGrid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 5rem;
}
.membersGrid-title {
  padding: 1rem 0;
  text-transform: uppercase;
}
.sectionStaff {
  padding: 10rem 0;
  background: #000000;
  color: #ffffff;
}

.staffContainer {
  display: flex;
  align-items: center;
  margin: 5rem 0;
}

.staffIntro {
  margin: 0 5rem 0 20rem;
  text-transform: uppercase;
  border-bottom: 0.2rem solid gold;
  padding-bottom: 1rem;
}

.sectionTips {
  background: #d8d8d8;
}
.sectionTips-head {
  text-align: center;
  margin: 5rem auto 1rem auto;
  width: 25%;
  text-transform: uppercase;
  border-bottom: 0.2rem solid #ffd700;
}
.sectionTips-copy {
  text-align: center;
}

.tipBoxes {
  display: flex;
  margin-top: 10rem;
  justify-content: center;
}
.tipBoxes-3 {
  margin-right: 0;
}

#map {
  width: 70%;
  height: 50rem;
  margin: 5rem auto;
}

