sources.styles\\components\\_mobileBtn.scss:
// mobile button & lines
.menuBtn {
  @include for(phone) {
    position: absolute;
    z-index: 2;
    left: 4rem;
    top: 7rem;
    padding: 0.5rem;
    cursor: pointer;
    @include easeOut;
    // lines
    &-line {
      width: 3rem;
      height: 0.4rem;
      margin: 0 0 0.6rem 0;
      background: $white;
      @include easeOut;
    }
    // mobile btn X close
    &.close {
      // rotate btn lines into X
      transform: rotate(180deg);
      // animation: rotate180 1s ease-in-out;
      .menuBtn-line {
        background: $brand-lightGold;
        // rotate line 1
        &:nth-child(1) {
          transform: rotate(45deg) translate(0.6rem, 0.4rem);
          // animation: rotate45 0.5s ease-in;
        }
        // hide line 2
        &:nth-child(2) {
          opacity: 0;
        }
        // rotate line 3
        &:nth-child(3) {
          transform: rotate(-45deg) translate(1rem, -0.8rem);
          // animation: rotateMinus45 0.5s ease-in;
        }
      }
    }
  }
}

